const { GoogleSpreadsheet } = require("google-spreadsheet");
const { JWT } = require("google-auth-library");

const spreadsheetController = async (req, res) => {
  // Initialize auth - see https://theoephraim.github.io/node-google-spreadsheet/#/guides/authentication
  const serviceAccountAuth = new JWT({
    // env var values here are copied from service account credentials generated by google
    // see "Authentication" section in docs for more info
    email: process.env.GOOGLE_CLIENT_EMAIL,
    key: process.env.GOOGLE_PRIVATE_KEY,
    scopes: ["https://www.googleapis.com/auth/spreadsheets"],
  });

  const doc = new GoogleSpreadsheet(
    "1wPJ22IagwOml8Au4qeHZ56KQ65dOnH560osSYrkDpS0",
    serviceAccountAuth
  );

  await doc.loadInfo(); // loads document properties and worksheets
  console.log(doc.title);
  // await doc.updateProperties({ title: 'renamed doc' });

  const sheet = doc.sheetsByTitle["UPDATE"]; // or use `doc.sheetsById[id]` or `doc.sheetsByTitle[title]`
  console.log(sheet.title);
  console.log(sheet.rowCount);
  const rows = await sheet.getRows({});
  console.log(rows.filter((row) => row.get("STATUS 2") == "UNPAID"));
  // adding / removing sheets
  // const newSheet = await doc.addSheet({ title: 'another sheet' });
  // await newSheet.delete();
  res.json({
    title: doc.title,
    colCount: sheet.columnCount,
  });
};

module.exports = spreadsheetController;
